import React from 'react';
import {Form, Button} from 'react-bootstrap';
import axios from 'axios';

class AddNewMovie extends React.Component {

    state = {
        imageURL: '',
        title: '',
        description: ''
    }

    myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    }

    postDataHandler = () => {
        const post = {
            imageURL: this.state.image,
            title: this.state.title,
            description: this.state.description
        }

        axios.post('http://localhost:3000/post');
    }

    render() {
        return (
            <React.Fragment>
                <h2>Pievienot jaunu filmu</h2>
                <Form className="mb-5">
                    <Form.Group controlId="">
                        <Form.Label>Image URL</Form.Label>
                        <Form.Control
                            type="text"
                            name="image"
                            placeholder="Image URL"
                            onChange={this.myChangeHandler}/>
                    </Form.Group>
                    <Form.Group controlId="">
                        <Form.Label>Title</Form.Label>
                        <Form.Control type="text" name="title" onChange={this.myChangeHandler} placeholder="Movie Title"/>
                    </Form.Group>
                    <Form.Group controlId="">
                        <Form.Label>Description</Form.Label>
                        <Form.Control type="text" name="description" onChange={this.myChangeHandler} placeholder="Movie Description"/>
                    </Form.Group>
                    <Button variant="primary" type="submit" onClick={this.postDataHandler}>
                        Submit
                    </Button>
                </Form>
            </React.Fragment>
        );
    }
}

export default AddNewMovie;
