import React, {useEffect, useState} from 'react';
import './App.css';
import List from './Movie/List';
import AddForm from './Movie/AddForm'
import withListLoading from './Movie/withListLoading';
import {Container, Row} from 'react-bootstrap';
import axios from 'axios';

function App() {

    function handleSubmit(event) {
        // Prevent default behavior
        event.preventDefault();

        const data = new FormData(event.target);
        // Access FormData fields with `data.get(fieldName)` For example, converting to
        // upper case
        data.set('username', data.get('username').toUpperCase());

        // Do your Axios stuff here
    }

    const ListLoading = withListLoading(List);
    const [appState, setAppState] = useState({loading: false, repos: null});

    useEffect(() => {
        setAppState({loading: true});
        const apiUrl = 'https://localhost:44368/products/get';
        axios
            .get(apiUrl)
            .then((repos) => {
                const allRepos = repos.data;
                setAppState({loading: false, repos: allRepos});
            });
    }, [setAppState]);

    return (
        <div className='App'>
            <Container className="mt-5">
                <Row className="justify-content-center">
                    <h1>Movie List</h1>
                </Row>

                <Row className="mt-5">
                    <ListLoading isLoading={appState.loading} repos={appState.repos}/>
                </Row>

                <Container className="mt-5">
                    <AddForm />
                </Container>
            </Container>
        </div>
    );
}

export default App;
