import React from 'react';
import {Form, Button} from 'react-bootstrap';
import axios from 'axios';

class AddNewMovie extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          username: '',
          age: null,
        };
      }

      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }

    state = {
        imageURL: '',
        title: '',
        description: ''
    }

    postDataHandler = () => {
        const post = {
            imageURL: this.state.image,
            title: this.state.title,
            description: this.state.description
        }

        axios.post('http://localhost:3000/post');
    }

    updateInputValue(evt) {
        this.setState({
          inputValue: evt.target.value
        });
      }

    render() {
        return (
            <React.Fragment>
                <h2>Pievienot jaunu filmu</h2>
                <Form className="mb-5">
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Image URL</Form.Label>
                        <Form.Control type="text" name="image" placeholder="Image URL" onChange={evt => this.updateInputValue(evt)}/>
                    </Form.Group>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Title</Form.Label>
                        <Form.Control type="text" name="title" placeholder="Movie Title"/>
                    </Form.Group>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Description</Form.Label>
                        <Form.Control type="text" name="description" placeholder="Movie Description"/>
                    </Form.Group>
                    <Button variant="primary" type="submit" onClick={this.postDataHandler}>
                        Submit
                    </Button>
                </Form>
            </React.Fragment>
        );
    }
}

export default AddNewMovie;
