import React from 'react';
import {Form, Button} from 'react-bootstrap';
import axios from 'axios';

class AddNewMovie extends React.Component {

    // state = {     imageURL: '',     title: '',     description: '' }
    // myChangeHandler = (event) => {     let nam = event.target.name;     let val =
    // event.target.value;     this.setState({[nam]: val});     console.log(nam) }
    // postDataHandler = () => {     const data = {         imageURL:
    // this.state.image,         title: this.state.title,         description:
    // this.state.description     }     alert(data);
    // axios.post('http://localhost:3000/post', data)      .then(response => {
    // console.log(response);      }); }

    render() {
        const initialFormData = Object.freeze({image: "", title: "", description: ""});

        const FooBarForm = () => {
            const [formData, updateFormData] = React.useState(initialFormData);

            const handleChange = (e) => {
                updateFormData({
                    ...formData,

                    // Trimming any whitespace
                    [e.target.name]: e
                        .target
                        .value
                        .trim()
                });
            };
        }

        const handleSubmit = (e) => {
            e.preventDefault()
            console.log(formData);
            // ... submit to API or something
        };
        
        return (
            <React.Fragment>
                <h2>Pievienot jaunu filmu</h2>
                <Form className="mb-5">
                    <Form.Group controlId="">
                        <Form.Label>Image URL</Form.Label>
                        <Form.Control
                            type="text"
                            name="image"
                            placeholder="Image URL"
                            onChange={handleChange}/>
                    </Form.Group>
                    <Form.Group controlId="">
                        <Form.Label>Title</Form.Label>
                        <Form.Control
                            type="text"
                            name="title"
                            onChange={handleChange}
                            placeholder="Movie Title"/>
                    </Form.Group>
                    <Form.Group controlId="">
                        <Form.Label>Description</Form.Label>
                        <Form.Control
                            type="text"
                            name="description"
                            onChange={handleChange}
                            placeholder="Movie Description"/>
                    </Form.Group>
                    <Button variant="primary" type="submit" onClick={handleSubmit}>
                        Submit
                    </Button>
                </Form>
            </React.Fragment>
        );
    }
}

export default AddNewMovie;
